mapper.select:

SELECT bisbn, btitle, bdate, bpage, bprice, bauthor, btranslator, bsupplement, bpublisher, Bookrental
FROM book
WHERE bisbn = #{bookISBN}

mapper.update:

UPDATE book SET bisbn = #{bisbn}, btitle = #{btitle}, bdate = #{bdate}, bprice=#{bprice},
bauthor= #{bauthor}, btranslator= #{btranslator}, bsupplement= #{bsupplement}, bpublisher= #{bpublisher},
bookRental= #{bookRental}
WHERE bisbn = #{bisbn}
